--

CREATE FUNCTION acs_event__shift_all(integer, interval, interval) RETURNS integer
    LANGUAGE plpgsql
    AS $_$
declare
        shift_all__event_id        alias for $1; -- default null,
        shift_all__start_offset    alias for $2; -- default 0,
