        'content_item',		   -- item_subtype
        'content_revision',		   -- content_type
        null,					   -- title
        null,					   -- description
        'text/plain',			   -- mime_type
        null,					   -- nls_language
        null,					   -- text
	p_storage_type				   -- storage_type
    );

    -- create an initial revision for the new attachment
    v_revision_id := acs_message__edit_file (
         v_file_id,				-- file_id        
