         p_mime_type,			-- mime_type      
         p_data,				-- data        
         p_creation_date,		-- creation_date  
         p_creation_user,		-- creation_user  
         p_creation_ip,			-- creation_ip    
         p_is_live				-- is_live        
    );

    return v_file_id;
end;$_$;


