  return new;
end;$$;


--
-- Name: cr_keywords_get_tree_sortkey(integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION cr_keywords_get_tree_sortkey(integer) RETURNS bit varying
    LANGUAGE plpgsql
    AS $_$
