    AS $_$
declare
    p_source_id         alias for $1;
    p_package_id        alias for $2;
    p_owner_id          alias for $3;
    p_feed_url          alias for $4;
    p_link              alias for $5;
    p_title             alias for $6;
    p_description       alias for $7;
    p_updates           alias for $8;
    p_last_scanned      alias for $9;
