                             p_creation_ip,
                             p_package_id
        );

        insert into na_sources (
                source_id, 
                package_id, 
                owner_id, 
                feed_url, 
                link, 
                title, 
                description, 
                updates, 
                last_scanned, 
                last_modified
        ) values (
                v_source_id,
                p_package_id, 
