                p_feed_url, 
                p_link, 
                p_title, 
                p_description, 
                p_updates, 
                p_last_scanned, 
                p_last_modified
        );

          PERFORM acs_permission__grant_permission(
          v_source_id,
          p_owner_id,
          'admin'
          );

        return v_source_id;

end;$_$;


