    v_entry_id             integer;
begin
    v_entry_id := acs_object__new (
        p_entry_id,
        'pinds_blog_entry',
        current_timestamp,
        p_creation_user,
        p_creation_ip,
        p_package_id
    );

    insert into pinds_blog_entries (
      entry_id, 
      package_id,
      title,
      title_url,
      category_id,
      content,
      content_format,
      entry_date,
      draft_p,
