    CONSTRAINT logger_variables_type_ck CHECK ((((type)::text = ('additive'::character varying)::text) OR ((type)::text = ('non-additive'::character varying)::text)))
);


--
-- Name: COLUMN logger_variables.type; Type: COMMENT; Schema: public; Owner: -
