    CONSTRAINT rc_rel_side_ck CHECK (((rel_side = 'one'::bpchar) OR (rel_side = 'two'::bpchar)))
);


--
-- Name: rc_segment_dependency_levels; Type: VIEW; Schema: public; Owner: -
